package kdata.test;

public class SecondTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// 변수 선언 => 자료형(데이터타입) 변수명;
		int a;
		a=10;
		// a의 값
		System.out.println(a);
		// a = 10
		System.out.println("a = " + a);
		
		int b;
		b=20;
		System.out.println(b);
		
		int c;
		c = a + b;
		// 10+20=30
		System.out.println(a + "+" + b + "=" + c );
		
		char ch;
		ch = 'A';
		ch = '가';
		System.out.println(ch);

	}

}
